using System;
using System.Diagnostics;

namespace ProjectEulerProductSeries2
{
    class Program
    {
        static void Main(string[] args)
        {
            double min = 100;
            double maxProduct = 1;
            double product, time;
            for (int a = 0; a < 200000; a++)
            {
                Stopwatch sw = Stopwatch.StartNew();
                char[] series = new char[1000];
                series = "".ToCharArray();
                
                for (short j = 0; j < series.Length - 12; j++)
                {
                    product = 1;
                    for (int k = j; k < j + 13; k++) product *= char.GetNumericValue(series[k]);
                    if (product > maxProduct) maxProduct = product;
                }
                sw.Stop();
                time = 1000 * sw.ElapsedTicks / (double)Stopwatch.Frequency;
                if (min > time) min = time;
            }
            Console.WriteLine(maxProduct);
            Console.WriteLine("Minimum time in milliseconds: " + min);
            Console.ReadKey();
        }
    }
}
